// --- Función auxiliar para buscar recursivamente una clave en un objeto o array JSON ---
function buscarValorPorClave(obj, claveBuscada) {
  if (typeof obj !== 'object' || obj === null) {
    return null;
  }
  if (obj.hasOwnProperty(claveBuscada)) {
    return obj[claveBuscada];
  }
  for (const key in obj) {
    if (obj.hasOwnProperty(key)) {
      const resultado = buscarValorPorClave(obj[key], claveBuscada);
      if (resultado !== null) {
        return resultado;
      }
    }
  }
  if (Array.isArray(obj)) {
    for (const item of obj) {
      const resultado = buscarValorPorClave(item, claveBuscada);
      if (resultado !== null) {
        return resultado;
      }
    }
  }
  return null;
}
// --- Fin de la función auxiliar ---


// Obtener el ítem de entrada
const item = $input.item;

// Suponemos que el contenido HTML está en item.json.content
const htmlInput = item.json.content;
let urlSD = null; // Variable para almacenar la URL encontrada

// Verificamos que el contenido sea una cadena (HTML)
if (typeof htmlInput !== 'string') {
  item.json.error = "El contenido recibido no es una cadena de texto (se esperaba HTML).";
  console.error(item.json.error);
  return item; // Devolver el ítem con un campo de error
}


// Expresión regular para encontrar etiquetas <script type="application/json"> y capturar su contenido
const scriptRegex = /<script type="application\/json"[^>]*>([\s\S]*?)<\/script>/g;
let match;

try {
  // Iteramos sobre todas las coincidencias de la expresión regular
  while ((match = scriptRegex.exec(htmlInput)) !== null) {
    const contenidoScript = match[1]; // El grupo 1 contiene el contenido dentro de la etiqueta <script>

    if (contenidoScript) {
      try {
        const datosJSON = JSON.parse(contenidoScript); // Intentamos parsear el texto como JSON

        // Buscamos dentro del JSON la clave específica
        const posibleUrl = buscarValorPorClave(datosJSON, 'browser_native_sd_url');

        // Si encontramos una URL válida (con extensión .mp4), la guardamos
        if (typeof posibleUrl === 'string' && posibleUrl.trim() !== '' && posibleUrl.includes('.mp4')) {
          urlSD = posibleUrl;
          console.log("URL SD encontrada dentro de una etiqueta <script>:", urlSD);
          break; // Salimos del bucle al encontrar la primera URL válida
        }

      } catch (errorJSON) {
        // Ignoramos scripts que no contienen JSON válido
        // console.warn("Script ignorado por error al parsear JSON:", errorJSON.message);
      }
    }
  }

} catch (error) {
  item.json.error = "Error durante el procesamiento con regex/JSON: " + error.message;
  console.error(item.json.error);
  return item; // Devolver el ítem con el error
}


// Añadir la URL extraída (o el error) al JSON del ítem
if (urlSD) {
  // Limpiar posibles entidades HTML como &amp;
  item.json.extracted_sd_url = urlSD.replace(/&/g, '&');
  console.log("URL extraída y limpiada correctamente:", item.json.extracted_sd_url);
  // Opcional: eliminar el contenido HTML original si ya no se necesita
  // delete item.json.content;
} else {
  item.json.error = "No se encontró 'browser_native_sd_url' usando regex y parseo JSON.";
  console.error(item.json.error);
}

// Devolver el ítem modificado
return item;

